mapper.insert:
INSERT INTO book (
	seq
	,name
	,writer_name
	,sub_title
	,title
	,explanation
	,publisher
	,category
)
VALUES (
	#{seq}
	,#{name}
	,#{writer_name}
	,#{sub_title}
	,#{title}
	,#{explanation}
	,#{publisher}
	,#{category}
)

		SELECT last_insert_id()

mapper.update:
    	UPDATE book
    	SET 	
    		seq = #{seq}
    		,name = #{name}
    		,writer_name = #{writer_name}
			,sub_title = #{sub_title}
			,title = #{title}
			,explanation = #{explanation}
			,publisher = #{publisher}
			,category = #{category}
		WHERE 1=1
			AND seq = #{seq}
