mapper.select:
SELECT b.seq ,b.ccg_seq ,(select name from CCG a where a.seq = b.ccg_seq) as name ,b.code ,b.codename_ko ,b.codename_en ,b.useNY ,b.delNY ,b.reg_date ,b.mod_date FROM NCT.CC b LEFT JOIN CCG a on b.ccg_seq = a.seq WHERE 1=1
AND b.delNY = #{shdelNY} AND b.useNY = #{shuseNY}
and b.seq = #{shseq} and a.name = #{shseq} and b.codename_ko LIKE CONCAT('%',#{shseq}, '%') and b.codename_en LIKE CONCAT('%',#{shseq}, '%')
AND b.reg_date BETWEEN #{shDateStart} AND #{shDateEnd} AND b.mod_date BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO CC (
	codename_ko
	,codename_en
	,reg_date
)
VALUES (
	#{codename_ko}
	,#{codename_en}
	,now()
)

		SELECT last_insert_id()
