mapper.sql:
	FROM CC 
	WHERE 1=1

AND b.delNY = #{shdelNY}
		AND b.useNY = #{shuseNY}



			and b.seq = #{shValue}
			and a.name = #{shValue}
			and b.codename_ko LIKE CONCAT('%',#{shValue}, '%')
			and b.codename_en LIKE CONCAT('%',#{shValue}, '%')



			AND b.reg_date BETWEEN #{shDateStart} AND #{shDateEnd}
			AND b.mod_date BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO CC (
	codename_ko
	,codename_en
	,reg_date
	,mod_date
)
VALUES (
	#{codename_ko}
	,#{codename_en}
	,now()
	,now()
)

		SELECT last_insert_id()

mapper.update:
UPDATE CC
SET
	ccg_seq = #{ccg_seq}
	,name = #{name}
	,codename_ko = #{codename_ko}
	,codename_en = #{codename_en}
	,useNY = #{useNY}
	,delNY = #{delNY}
	,reg_date = now()
	,mod_date = now()
WHERE 1=1
	AND seq = #{seq}
