mapper.select:
SELECT a.seq ,a.name ,a.name_en , (SELECT COUNT(b.ccg_seq) FROM CC b WHERE a.seq = b.ccg_seq)as code_count ,a.reg_date ,a.mod_date ,a.useNY ,a.delNY FROM NCT.CCG a WHERE 1=1
AND a.delNY = #{shdelNY} AND a.useNY = #{shuseNY}
AND a.seq = #{shseq} AND a.name LIKE CONCAT('%',#{shseq}, '%') AND a.name_en LIKE CONCAT('%',#{shseq}, '%')
AND a.reg_date BETWEEN #{shDateStart} AND #{shDateEnd} AND a.mod_date BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO CCG ( name ,name_en ,reg_date ,useNY ,delNY
) VALUES ( #{name} ,#{name_en} ,now() ,1 ,0 )
SELECT last_insert_id()
