mapper.sql:
FROM member a
WHERE 1=1

AND a.career = #{shcareer}
AND a.certifiNY = #{shcertifiNY}
 AND a.useNY = #{shuseNY}
 AND a.delNY = #{shdelNY}
 AND a.job = #{shjob}



	and a.seq = #{shValue}
	and a.memberName LIKE CONCAT('%',#{shValue},'%')	
	and a.memberID LIKE CONCAT('%',#{shValue},'%')

mapper.update:
UPDATE member
SET
	memberID = #{memberID}
	,pwd = #{pwd}
	,job = #{job}
	,career = #{career}
	,memberName= #{memberName}
	,memberName_en = #{memberName_en}
	,dob = #{dob}
	,telecom = #{telecom}
	,phone = #{phone}
	,phone_emer = #{phone_emer}
	,email = #{email}
	,email_code = #{email_code}
	,hobby = #{hobby}
	,region = #{region}
	,certifiNY = #{certifiNY}
	,useNY = #{useNY}
	,delNY = #{delNY}
WHERE 1=1
	AND seq = #{seq}
