mapper.insert:
INSERT INTO member (
	memberID
	,pwd
	,job
	,career
	,memberName
	,memberName_en

	,telecom
	,phone
	,email
	,email_code
	,hobby
	,region
	,certifiNY
	,genre
	,useNY
	,delNY
) VALUES (
	#{memberID}
	,#{pwd}
	,#{job}
	,#{career}
	,#{memberName}
	,#{memberName_en}

	,#{telecom}
	,#{phone}
	,#{email}
	,#{email_code}

	,#{hobby}
	,#{region}
	,#{certifiNY}
	,#{genre}
	,1
	,0
)

		SELECT last_insert_id()

mapper.update:
UPDATE member
SET
	memberID = #{memberID}
	,pwd = #{pwd}
	,job = #{job}
	,career = #{career}
	,memberName= #{memberName}
	,memberName_en = #{memberName_en}

	,telecom = #{telecom}
	,phone = #{phone}
	,email = #{email}
	,email_code = #{email_code}
	,hobby = #{hobby}
	,region = #{region}
	,certifiNY = #{certifiNY}
	,genre = #{genre}
	,useNY = #{useNY}
	,delNY = #{delNY}
WHERE 1=1
	AND seq = #{seq}
